    // ACTIVITY METHODS.
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        // Initialize.
        ewbBody = findViewById(R.id.body);

        // Populate.
        ewbBody.setContent(SAMPLE_CONTENT); // Set Content.
    }

    @Override
    public boolean onCreateOptionsMenu(Menu menu) {
        getMenuInflater().inflate(R.menu.menu_main, menu);
        return true;
    }

    @Override
    public boolean onOptionsItemSelected(MenuItem item) {
        if (item.getItemId() == R.id.get_content) {
            getContentFromEditableWenView();
            return true;
        } else {
            return false;
        }
    }

    // PRIVATE METHODS.
    private void getContentFromEditableWenView() {
        // Get Content.
        ewbBody.getContent(new EditableWebView.GetContentListener() {
            @Override
            public void onContentReceived(@NonNull String content) {
                Toast.makeText(MainActivity.this, content, Toast.LENGTH_SHORT).show();
            }
        });
    }
